"use strict";(function(){var g=document.querySelector(".map-info-btn");var b=document.querySelector(".pop-up");var i=document.querySelector(".pop-up-cross");var a=b.querySelector("form");var h=b.querySelector("[name=your-name]");var d=b.querySelector("[type=email]");var e=true;var f="";try{f=localStorage.getItem("login")}catch(c){e=false}g.addEventListener("click",function(j){j.preventDefault();b.classList.add("pop-up-show");h.focus();if(f){h.value=f;d.focus()}else{h.focus()}});i.addEventListener("click",function(j){j.preventDefault();b.classList.remove("pop-up-show");b.classList.remove("pop-up-error")});a.addEventListener("submit",function(j){if(!h.value||!d.value){j.preventDefault();b.classList.remove("pop-up-error");b.offsetWidth=b.offsetWidth;b.classList.add("pop-up-error")}else{if(e){localStorage.setItem("login",h.value)}}});window.addEventListener("keydown",function(j){if(j.keyCode===27){if(b.classList.contains("pop-up-show")){j.preventDefault();b.classList.remove("pop-up-show")}}})})();